using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Data;
using System.Data.OleDb;


namespace dbTurnos.DataSQL
{
    /// <summary>DataTable que se cargar con lo datos obtenidos a partir de una sentencia SQL.</summary>
    public class AutoGeneratedDataTable : DataTable
    {
        //TODO: extraer cadena conexion para abstraer clase

        private string _consulta;
        public string ConsultaSQL { get { return _consulta; } set { _consulta = value; } }

        /// <summary>Crea una instancia de AutoGeneratedDataTable, con los datos especificados por la sentencia SQL.</summary>
        /// <exception cref="System.InvalidOperationException"></exception>
        /// <exception cref="System.Data.OleDb.OleDbException"></exception>
        public AutoGeneratedDataTable(string consulta)
            : base()
        {
            _consulta = consulta;
            Refrescar();
        }

        //TODO: elimar, metodo para test/debbug
        public AutoGeneratedDataTable show()
        {
            new Form2(this).ShowDialog();
            return this;
        }

        /// <summary>ACtualiza los datos del DataTable segun la Base de Datos.</summary>
        /// <exception cref="System.InvalidOperationException"></exception>
        /// <exception cref="System.Data.OleDb.OleDbException"></exception>
        public void Refrescar()
        {
            using (OleDbConnection conexion = new OleDbConnection (new Properties.Settings().Bar_TurnosConnectionString))
            {
                 OleDbCommand comandoSql = new OleDbCommand(_consulta, conexion);
                try
                {
                    conexion.Open();
                    OleDbDataAdapter da = new OleDbDataAdapter(comandoSql);
                    Clear();
                    da.Fill(this);
                }
                finally
                {
                    conexion.Close();
                }
            }
        }
    }
}
